mapper.select:
select * from product_img
where pid = #{pid}
order by pdepth

select * from ( SELECT pa.* ,(#{page} * 6 + 6) as totalcount, rownum rn FROM ( select pi.pimg,p.pname, p.pprice, p.pdiscount,p.pcno,p.pid ,p.stag,pc.pcategory,pc.pcno_top from products p join (select pimg, pid from product_img where pdepth =0 ) pi on pi.pid=p.pid join product_category pc on p.pcno = pc.pcno and pc.pcno_top = #{pcno_top}
p.pcno = #{pcno}
pprice desc pprice asc pcount desc pcount asc
) pa ) where rn between (#{page} * 6 + 1) AND (#{page} * 6 + 6)
